import React from 'react';
import './CommentItem'



export const CommentItem = ({ item } = {}) => {
  return (
    <div className="comment-item">
      <div className="comment-item_author">{item.user.name} {item.user.surname}</div>
      <div className="comment-item_text"> {item.text}</div>
    </div>
  );
};
