import React from 'react';
import  './ListItem.scss'

{
  (function () {

    const cropElement = document.querySelectorAll('.post_item_text'), // выбор элементов 
      size = 120                                             // кол-во символов 
    endCharacter = '...';                                  // окончание 

    cropElement.forEach(el => {
      let text = el.innerHTML;

      if (el.innerHTML.length > size) {
        text = text.substr(0, size);
        el.innerHTML = text + endCharacter;
      }
    });

  }());
}

export const ListItem = ({ item } = {}) => {
  return (
    <div className="post_item">
      <div className="post_item_img">
        <img src={item?.imageUrl}></img>
      </div>
      <div className="post_item_body">
        <div className="post_item_title">
          {item.name}
        </div>
        <div className="post_item_text">
          {item.text}
          <span className="post_item_author"> by {item.user?.name}</span>
        </div>
        <div className="post_item_comments">
          comments here
        </div>

      </div>

    </div>
  );
};
